import './App.css';
import React, { useState } from 'react';
import { library } from '@fortawesome/fontawesome-svg-core'
import { fas } from '@fortawesome/free-solid-svg-icons'
import { faTwitter, faFontAwesome } from '@fortawesome/free-brands-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import LogIn from './LogIn/LogIn.js';
import Register from './Register/Register.js';
import Place from './Place/Place.js'
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import UserHomePage from './userHomePage/UserHomePage.js'
import {Search} from './Search/Search.js'

library.add(fas, faTwitter, faFontAwesome)

class User extends React.Component {
  constructor(props) {
    super(props);
    this.state = { click: false }
    this.click = this.click.bind(this);
  }
  click() {
    if (!this.state.click) {
      this.setState({
        click: true
      })
    } else {
      this.setState({
        click: false
      })
    }
  }
  render() {
    if (!this.state.click) {
      return (
        <div className='user'>
          <button onClick={this.click} type="button" className="btn btn-outline-primary rounded-5">
            <FontAwesomeIcon icon="fa-solid fa-user" />
          </button>
        </div>
      );
    } else {
      return (
        <LogIn handleLogIn={this.props.handleLogIn} close={this.click} click={this.state.click} />
      );
    }

  }
}

function VisitantHomePage( { handleLogIn } ) {
  return(
    <div id="homepage" className='conteiner-fluid'>
      <div />
      <Search />
      <User handleLogIn={handleLogIn} />
    </div>
  )
}

function HomePage() {
  const [user, setUser] = useState(null)
  return (
    <div className='vh-100 container-fluid'>
      {user? <UserHomePage user={user} /> : <VisitantHomePage handleLogIn={setUser} />}
    </div>

  );
}

function App() {
  return (
    <div className="App">
      <Router>
        <Routes>
          <Route path='/register' element={<Register />} />
          <Route path='/' element={<HomePage />} />
          <Route path='/place' element={<Place />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
