import React from "react";
import styles from './Place.module.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const user = {
    nombre: "Sebastian",
    saldo: 15000,
}

const place = {
    name: "Pueblo Limite",
    subscriber: false,
    status: "owner",
    location: "Av. Brasil 2020",
    description: "Boliche para pasarla espectacular",
    products: {
        "Combos": [{
            id: "1",
            prod_name: "Combo Absolute",
            price: 15000,
            img: "place.jpeg"
        }],
        "Sin Alcohol": [{
            id: "2",
            prod_name: "Botella Agua",
            price: 1000,
            img: "place.jpeg"
        }, {
            id: "5",
            prod_name: "Coca Cola",
            price: 1000,
            img: "place.jpeg"
        },
        {
            id: "6",
            prod_name: "Speed",
            price: 1000,
            img: "place.jpeg"
        }], "Espumantes": [{
            id: "3",
            prod_name: "Baron B",
            price: 20000,
            img: "place.jpeg"
        }, {
            id: "4",
            prod_name: "Chandon",
            price: 15000,
            img: "place.jpeg"
        }]
    }
}

class ProfileHeader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            click: false,
        }
        this.click = this.click.bind(this);
        this.handleOverlayClick = this.handleOverlayClick.bind(this)
    }

    click() {
        if (this.state.click)
            this.setState({ click: false })
        else
            this.setState({ click: true })
    }

    handleOverlayClick(e) {
        // Cierra el popup solo si el clic ocurre en el fondo (overlay)
        if (Array.from(e.target.classList).some((clase) => /overlay/.test(clase))) {
            this.click();
        }
    }

    render() {
        if (!this.state.click) {
            return (
                <div onClick={this.click} className={styles.place_profile_header}>
                    <p>{user.nombre} <FontAwesomeIcon icon="fa-solid fa-chevron-down" /></p>
                </div>
            );
        }
        return (
            <div className={styles.overlay} onClick={this.handleOverlayClick}>
                <div className={styles.popup}>
                    <div className={styles.popup_options}>
                        <div className={styles.popup_option}>
                            <p>Saldo</p>
                            <p>{user.saldo}</p>
                        </div>
                        <div className={styles.popup_option}>
                            <p>Mis qrckets</p>
                            <p>6</p>
                        </div>
                        <div className={styles.popup_option}>
                            <p>Suscripciones</p>
                            <p>7</p>
                        </div>
                        <div className={styles.popup_option}>
                            <p>Mas opciones</p>
                            <p></p>
                        </div>
                    </div>
                    <div onClick={this.click} className={styles.popup_profile}>
                        <p>{user.nombre} <FontAwesomeIcon icon="fa-solid fa-chevron-up" /></p>
                    </div>
                </div>
            </div>
        );
    }
}

class ProdCarousel extends React.Component {
    constructor(props) {
        super(props);
    }
    render() {
        const data = this.props.data;
        return (
            <div>
                <p>{data.prod_name}</p>
                <img src={data.img} />
                <p>{data.price}</p>
            </div>
        );
    }
}

class Carousel extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <div>
                {this.props.products.map(prod => <ProdCarousel key={prod.id} data={prod} />)}
            </div>
        );
    }
}

class Store extends React.Component {
    constructor(props) {
        super(props);
    }


    render() {
        products = place.products;
        return (
            <div>
                {Object.keys(products).map((prod_cat, idx) => <Carousel key={idx} products={products.prod_cat}/>)}
            </div>
        );
    }
}

class Info extends React.Component {
    render() {
        return (
            <div>

            </div>
        );
    }
}

class Place extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            store: true
        }
    }

    render() {
        return (
            <div>
                <ProfileHeader />
                <div className={styles.place_header}>
                    <div className={styles.place_header_profile}>
                        <img src="place.jpeg" alt="PuertoNorte" />
                        <div>
                            <p>Puerto Norte</p>
                            <button type="button" className="btn btn-danger">Suscribirse</button>
                        </div>
                    </div>
                    <div className={styles.place_options}>
                        <button>Tienda</button>
                        <button>Info</button>
                    </div>
                </div>
                <div className={styles.place_options}>
                    {this.state.place ?
                        <Store />
                        :
                        <Info />}
                </div>
            </div>
        );
    }
}

export default Place;
