import React, { useState } from "react";
import styles from './Place.module.css';
import Carousel from "./Carousel/Carousel.js";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import qrcode from "qrcode-generator";

const user = {
    nombre: "Sebastian",
    saldo: 15000,
    qrckets_activos: [{
        codigoqr: "dedkeofoefjofj",
        codigo: "abcdg",
        color: "#fff",
        nombre: "Botella Agua"
    },
    {
        codigoqr: "sfseofkspeofksekfs",
        codigo: "abcde",
        color: "#ffg",
        nombre: "Combo Absolut"
    },
    {
        codigoqr: "selkfsefmsfsfofs",
        codigo: "afcde",
        color: "#gfg",
        nombre: "Chandon Delice"
    }
    ],
    qrckets_usados: [{
        codigoqr: "aebtrerrebrtrbt",
        codigo: "abcdbg",
        color: "#fff",
        nombre: "Combo Smirnoff"
    },
    {
        codigoqr: "areretertgrtu",
        codigo: "abdccde",
        color: "#ffg",
        nombre: "Speed"
    },
    {
        codigoqr: "grtsryreyrtuyi",
        codigo: "dddd",
        color: "#gfg",
        nombre: "Imperial Ipa"
    }
    ]
}

const place = {
    name: "Pueblo Limite",
    subscriber: false,
    status: "owner",
    location: "Av. Brasil 2020",
    description: "Boliche para pasarla espectacular",
    products: {
        "Combos": [{
            id: "1",
            prod_name: "Combo Absolute",
            price: 15000,
            img: "place.jpeg"
        }],
        "Sin Alcohol": [{
            id: "2",
            prod_name: "Botella Agua",
            price: 1000,
            img: "place.jpeg"
        }, {
            id: "5",
            prod_name: "Coca Cola",
            price: 1000,
            img: "place.jpeg"
        },
        {
            id: "6",
            prod_name: "Speed",
            price: 1000,
            img: "place.jpeg"
        }],
        "Espumantes": [{
            id: "3",
            prod_name: "Baron B",
            price: 20000,
            img: "place.jpeg"
        }, {
            id: "4",
            prod_name: "Chandon",
            price: 15000,
            img: "place.jpeg"
        }],
        "Artesanal": [{
            id: "2",
            prod_name: "Honey",
            price: 1000,
            img: "place.jpeg"
        }, {
            id: "5",
            prod_name: "Ipa",
            price: 1000,
            img: "place.jpeg"
        },
        {
            id: "6",
            prod_name: "Negra",
            price: 1000,
            img: "place.jpeg"
        }]
    }
}
const products = place.products;

class ProfileHeader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            click: false,
        }
        this.click = this.click.bind(this);
        this.handleOverlayClick = this.handleOverlayClick.bind(this)
    }

    click() {
        if (this.state.click)
            this.setState({ click: false })
        else
            this.setState({ click: true })
    }

    handleOverlayClick(e) {
        // Cierra el popup solo si el clic ocurre en el fondo (overlay)
        if (Array.from(e.target.classList).some((clase) => /overlay/.test(clase))) {
            this.click();
        }
    }

    render() {
        if (!this.state.click) {
            return (
                <div onClick={this.click} className={styles.place_profile_header}>
                    <p>{user.nombre} <FontAwesomeIcon icon="fa-solid fa-chevron-down" /></p>
                </div>
            );
        }
        return (
            <div className={styles.overlay} onClick={this.handleOverlayClick}>
                <div className={styles.popup}>
                    <div className={styles.popup_options}>
                        <div className={styles.popup_option}>
                            <p>Saldo</p>
                            <p>{user.saldo}</p>
                        </div>
                        <div className={styles.popup_option}>
                            <p>Mis qrckets</p>
                            <p>6</p>
                        </div>
                        <div className={styles.popup_option}>
                            <p>Suscripciones</p>
                            <p>7</p>
                        </div>
                        <div className={styles.popup_option}>
                            <p>Mas opciones</p>
                            <p></p>
                        </div>
                    </div>
                    <div onClick={this.click} className={styles.popup_profile}>
                        <p>{user.nombre} <FontAwesomeIcon icon="fa-solid fa-chevron-up" /></p>
                    </div>
                </div>
            </div>
        );
    }
}

class Store extends React.Component {
    render() {
        return (
            <div className={styles.store}>
                {Object.keys(products).map((prod_cat, idx) => <div key={idx} className={styles.category_card} >
                    <p>{prod_cat}</p>
                    <Carousel products={products[prod_cat]} />
                </div>)}
            </div>
        );
    }
}


class QrPage extends React.Component {
    render() {
        return (
            <div className={styles.qr_page}>
                <p>Tus QR activos</p>
                <QrList qrs={user.qrckets_activos} state={"active"} />
                <p>QR escaneados</p>
                <QrList qrs={user.qrckets_usados} state={"used"} />
            </div>
        );
    }

}

function QrPopUp( {qr} ) {

    var typeNumber = 4;
    var errorCorrectionLevel = 'L';
    var qrc = qrcode(typeNumber, errorCorrectionLevel);
    qrc.addData(qr.name);
    qrc.make();
    var img = qrc.createImgTag()
    return(
        <div className={styles.qr_overlay}>
            <div className={styles.qr_popup}>
                {/*img*/}
            </div>
        </div>
    );
}

function Qr({ qr }) {
    const [state, setState] = useState({ click: false })

    const click = () => {setState({click:true})}

    return (
        <div className={styles.qr_card} onClick={() => click()}>
            <div className={styles.qr_card_img}>
                <img src="qr.png" alt="qr" />
            </div>
            <div className={styles.qr_card_info}>
                <p>{qr.nombre}</p>
            </div>
            { state.click? <QrPopUp qr={qr}/>: null}
        </div>
    );
}



function QrList({ qrs, state }) {
    return (
        <div className={styles.qr_list}>
            {qrs.map(qr => <Qr qr={qr} />)}
        </div>
    );
}

class Place extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            store: true,
        }

        this.radioChange = this.radioChange.bind(this);
    }

    radioChange(e) {
        const value = e.target.value;
        if (value === "store")
            this.setState({ store: true })
        else
            this.setState({ store: false })

    }

    render() {
        return (
            <div>
                <ProfileHeader />
                <div className={styles.place_header}>
                    <div className={styles.place_header_profile}>
                        <img src="place.jpeg" alt="PuertoNorte" />
                        <div>
                            <p>Puerto Norte</p>
                            <button type="button" className="btn btn-danger">Suscribirse</button>
                        </div>
                    </div>
                    <div className={styles.place_options}>
                        <label className={styles.place_options_label} onChange={this.radioChange} >
                            <input type="radio" name="place" value="store" defaultChecked />
                            Store
                            <div className={styles.place_options_line} />
                        </label>
                        <label className={styles.place_options_label} onChange={this.radioChange}>
                            <input type="radio" name="place" value="qrckets" />
                            Qrckets
                            <div className={styles.place_options_line} />
                        </label>
                    </div>
                </div>
                <div className={styles.place_options}>
                    {this.state.store ?
                        <Store />
                        :
                        <QrPage />}
                </div>
            </div>
        );
    }
}

export default Place;
