import React from 'react';
import styles from './LogIn.module.css'

class LogIn extends React.Component {
	constructor(props) {
		super(props);
		this.handleOverlayClick = this.handleOverlayClick.bind(this);
	}

	handleOverlayClick(e) {
    // Cierra el popup solo si el clic ocurre en el fondo (overlay)
    if (Array.from(e.target.classList).some((clase) => /overlay/.test(clase))) {
      this.props.click();
    }
  }

	render() {
		return (
			<div className={styles.overlay} onClick={this.handleOverlayClick}>
				<div className={styles.popup}>
					<p>Iniciar Sesión</p>
					<form className='d-flex flex-column justify-content-between'>
						<input className="form-control rounded-5" type="text" placeholder="username" aria-label="default input example"></input>
						<input className="form-control rounded-5" type="password" placeholder="password" aria-label="default input example"></input>
						<div className="d-grid gap-2">
							<button className="btn btn-primary rounded-5" type="button">Ingresar</button>
						</div>
					</form>
					<div>
						<p>¿No tenés cuenta?</p>
						<a href="">Registrarse</a>
					</div>

				</div>


			</div>
		);
	}
}

export default LogIn;
