import React from "react";
import styles from "./Product.module.css";

function Product({data, closeClick}) {
    const handleOverlayClick = (e) => {
        // Cierra el popup solo si el clic ocurre en el fondo (overlay)
        if (Array.from(e.target.classList).some((clase) => /overlay/.test(clase))) {
            closeClick();
            e.stopPropagation();
        }
    }
    return(
            <Popup close={closeClick} up={false}>

                    <div className={styles.product_card}>
                        <img src={data.img} alt={data.prod_name}/>
                        <div className={styles.product_info}>
                            <p className={styles.product_name}>{data.prod_name}</p>
                            <p className={styles.product_price}>${data.price}</p>
                        </div>
                        <div>
                            <button className={styles.product_fastbuy}>Compra Rapida</button>
                        </div>
                    </div>
            </Popup>
    )
}

export default Product;
