import React, { useState } from "react";
import styles from "./Carousel.module.css"

function ProdCarousel({data}) {
    const [state, setState] = useState({there_is_click: false,
                                        product_data: {} });

    const productClick = (prod_data) => {
        setState({
            there_is_click: true, 
            product_data: prod_data
            });
    }
{state.there_is_click? <Product data={state.product_data}/> : null }
    return (
        <div onClick={productClick(data)} className={styles.prod_carousel}>
            <img src={data.img} alt="img" />
            <div className={styles.prod_carousel_header}><p>{data.prod_name}</p><p>${data.price}</p></div>
        </div>
    );
}

function Carousel({props}) {
    return (
        <div className={styles.carousel}>
            {props.products.map(prod => <ProdCarousel key={prod.id} data={prod} click={props.click} />)}
        </div>
    );
}

export default Carousel;
