import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import styles from './Register.module.css'
import registerService from './registerService'

function SuccessRegister({ name }) {
	<div className={styles.reg_container}>
		<h1>{name} te has registrado correctamente!</h1>
		<Link to="/">Iniciar sesion</Link>
	</div>
}

function Register() {

	const [inputs, setInputs] = useState({
		name: '',
		lastname: '',
		email: '',
		password: '',
	})

	const [repeatPassword, setRepeatPassword] = useState('');
	const [errorMessage, setErrorMessage] = useState(null);
	const [success, setSuccess] = useState(false);

	const handleSubmit = async (e) => {
		e.preventDefault();

		if (inputs.password !== repeatPassword) {
			setErrorMessage({ repeatPassword: true })
			return;
		}

		try {
			result = await registerService.register(inputs);
			console.log(result)
			if (result.error) {
				if(result.error.email)
					setErrorMessage({ email: true });
				return;
			}

			if (result.success) {
				setSuccess(true);
			}

		} catch (error) {
			console.error("There was an error: " + error);
		}

	}

	const handleInputChange = (e) => {
		setInputs( {
			...inputs,
			[e.target.name]: e.target.value
		} )
	}

	return !success ?
		<div className={styles.reg_container}>
			<p>Registrarse</p>
			<div className={styles.link}>
				<p>¿Ya tenés una cuenta?</p>
				<Link to="/">Ingresa aquí</Link>
			</div>
			<form className={styles.form} onSubmit={handleSubmit}>
				<input className="form-control rounded-5" onChange={handleInputChange} name="name" type="text" placeholder='name' value={inputs.name}/>
				<input className="form-control rounded-5" onChange={handleInputChange} name="lastname" type="text" placeholder='lastname' value={inputs.lastname}/>
				<input className="form-control rounded-5" onChange={handleInputChange} name="email" type="email" placeholder='email' value={inputs.email}/>
				<input className="form-control rounded-5" onChange={handleInputChange} name="password" type="password" placeholder="password" value={inputs.password} />
				<input className="form-control rounded-5" onChange={({ target }) => setRepeatPassword(target.value)} name="password" type="password" placeholder="repeat password" value={repeatPassword} />
				<div className="d-grid gap-2">
					<button className="btn btn-primary rounded-5" type="submit">Registrarse</button>
				</div>
			</form>
		</div> :
		<SuccessRegister name={inputs.name} />

}

export default Register;
