import { useState } from "react";
import { useNavigate } from "react-router-dom";
import styles from './Search.module.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const url = "http://localhost:1234"

function LinkPage({ place }) {
  const navigate = useNavigate();
  const handlePlaceClick = (e) => {
    e.preventDefault();
  }

  return (
    <div className={styles.search_places_linkplace} onClick={handlePlaceClick}>
      <div className={styles.search_places_linkplace_img}>
        <img src={place.img} alt={place.name} />
      </div>
      <div className={styles.search_places_linkplace_info}>
        <p className={styles.search_places_linkplace_name}>{place.name}</p>
        <p className={styles.search_places_linkplace_location}>{place.location}</p>
      </div>
    </div>
  );
}

function SearchPlaces({ places }) {
  return (
    <div className={styles.search_places}>
      {places.map(place =>
        <LinkPage key={place.name} place={place} />)
      }
    </div>
  );
}


export function Search({ user, handlePlace }) {

  const [click, setClick] = useState(true);
  const [input, setInput] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [places, setPlaces] = useState([])

  const handleInputChange = (e) => {
    e.preventDefault();
    setInput(e.target.value);
  }

  const handleButtonClick = () => {
    fetch(url + "/places/" + input).then(response => {
      if (!response.ok) setErrorMessage("Error en solicitud HTTP");
      return response.json();
    }).then(data => {
      if (data.state) setPlaces(data.places)
    }).catch(error => {
      setErrorMessage("Error en submit: " + error);
    });

    if (errorMessage) console.log(errorMessage);
    console.log(places);
  }

  if (click) {
    return (
      <div className={styles.search}>
        <button onClick={() => setClick(false)} type="button" className="btn btn-outline-primary rounded-5">
          <FontAwesomeIcon icon="fa-solid fa-magnifying-glass" />
        </button>
      </div>
    );
  }
  return (
    <div className={styles.search}>
      <form>
        <input onChange={handleInputChange} value={input} className="form-control rounded-5 border-primary text-center" type="text" placeholder="Place" aria-label="default input example"></input>
        <button type="button" onClick={handleButtonClick}></button>
      </form>
      {places ? <SearchPlaces places={places} /> : null}
    </div>
  );
}
