import React from 'react'
import styles from './Popup.module.css'
import clsx from 'clsx';

export function Popup(props) {
    const handleOverlayClick = (e) => {
		// Cierra el popup solo si el clic ocurre en el fondo (overlay)
		if (Array.from(e.target.classList).some((clase) => /overlay/.test(clase))) {
			props.close();
		}
	}

    const style = clsx(styles.overlay, {
        [styles.overlay-up]: props.up,
        [styles.overlay-bottom]: props.bottom,
    })        

	return (
		<div className={style} onClick={handleOverlayClick}>
			<div className={styles.popup}>
                {props.children}
			</div>
		</div>
	);
}
