import React from "react";
import styles from './Place.module.css';
import Carousel from "./Carousel/Carousel.js";
import Product from "./Product/Product.js"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const user = {
    nombre: "Sebastian",
    saldo: 15000,
}

const place = {
    name: "Pueblo Limite",
    subscriber: false,
    status: "owner",
    location: "Av. Brasil 2020",
    description: "Boliche para pasarla espectacular",
    products: {
        "Combos": [{
            id: "1",
            prod_name: "Combo Absolute",
            price: 15000,
            img: "place.jpeg"
        }],
        "Sin Alcohol": [{
            id: "2",
            prod_name: "Botella Agua",
            price: 1000,
            img: "place.jpeg"
        }, {
            id: "5",
            prod_name: "Coca Cola",
            price: 1000,
            img: "place.jpeg"
        },
        {
            id: "6",
            prod_name: "Speed",
            price: 1000,
            img: "place.jpeg"
        }],
        "Espumantes": [{
            id: "3",
            prod_name: "Baron B",
            price: 20000,
            img: "place.jpeg"
        }, {
            id: "4",
            prod_name: "Chandon",
            price: 15000,
            img: "place.jpeg"
        }],
        "Artesanal": [{
            id: "2",
            prod_name: "Honey",
            price: 1000,
            img: "place.jpeg"
        }, {
            id: "5",
            prod_name: "Ipa",
            price: 1000,
            img: "place.jpeg"
        },
        {
            id: "6",
            prod_name: "Negra",
            price: 1000,
            img: "place.jpeg"
        }]
    }
}
const products = place.products;

class ProfileHeader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            click: false,
        }
        this.click = this.click.bind(this);
        this.handleOverlayClick = this.handleOverlayClick.bind(this)
    }

    click() {
        if (this.state.click)
            this.setState({ click: false })
        else
            this.setState({ click: true })
    }

    handleOverlayClick(e) {
        // Cierra el popup solo si el clic ocurre en el fondo (overlay)
        if (Array.from(e.target.classList).some((clase) => /overlay/.test(clase))) {
            this.click();
        }
    }

    render() {
        if (!this.state.click) {
            return (
                <div onClick={this.click} className={styles.place_profile_header}>
                    <p>{user.nombre} <FontAwesomeIcon icon="fa-solid fa-chevron-down" /></p>
                </div>
            );
        }
        return (
            <div className={styles.overlay} onClick={this.handleOverlayClick}>
                <div className={styles.popup}>
                    <div className={styles.popup_options}>
                        <div className={styles.popup_option}>
                            <p>Saldo</p>
                            <p>{user.saldo}</p>
                        </div>
                        <div className={styles.popup_option}>
                            <p>Mis qrckets</p>
                            <p>6</p>
                        </div>
                        <div className={styles.popup_option}>
                            <p>Suscripciones</p>
                            <p>7</p>
                        </div>
                        <div className={styles.popup_option}>
                            <p>Mas opciones</p>
                            <p></p>
                        </div>
                    </div>
                    <div onClick={this.click} className={styles.popup_profile}>
                        <p>{user.nombre} <FontAwesomeIcon icon="fa-solid fa-chevron-up" /></p>
                    </div>
                </div>
            </div>
        );
    }
}

class Store extends React.Component {
    constructor() {
        super();
        this.state = {
            product_data: {} 
        }
        this.productClick = this.productClick.bind(this);
    }

    productClick(prod_data) {
        this.setState({product_data: prod_data});
    }
    render() {
        return (
            <div className={styles.store}>
                {Object.keys(products).map((prod_cat, idx) => <div className={styles.category_card} style={{width: (products[prod_cat].length * 90).toString() + "vw"}}>
                                                                <p>{prod_cat}</p>
                                                                <Carousel key={idx} click={this.productClick} products={products[prod_cat]} />
                                                              </div>)}
            {this.state.product_data.keys.length > 0? <Product data={this.state.product_data}/> : null}
            </div>
        );
    }
}


class Info extends React.Component {
    render() {
        return (
            <div className={styles.info}>

            </div>
        );
    }
}

class Place extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            store: true,
            product: false
        }
    }

    render() {
        return (
            <div>
                <ProfileHeader />
                <div className={styles.place_header}>
                    <div className={styles.place_header_profile}>
                        <img src="place.jpeg" alt="PuertoNorte" />
                        <div>
                            <p>Puerto Norte</p>
                            <button type="button" className="btn btn-danger">Suscribirse</button>
                        </div>
                    </div>
                    <div className={styles.place_options}>
                        <button>Tienda</button>
                        <button>Info</button>
                    </div>
                </div>
                <div className={styles.place_options}>
                    {this.state.store ?
                        <Store />
                        :
                        <Info />}
                </div>
            </div>
        );
    }
}

export default Place;
