import React from 'react';
import styles from './LogIn.module.css'
import { Link } from 'react-router-dom'


function LogIn(props) {

	const handleOverlayClick = (e) => {
		// Cierra el popup solo si el clic ocurre en el fondo (overlay)
		if (Array.from(e.target.classList).some((clase) => /overlay/.test(clase))) {
			props.close();
		}
	}
	return (
		<div className={styles.overlay} onClick={this.handleOverlayClick}>
			<div className={styles.popup}>
				<p>Iniciar Sesión</p>
				<form className={styles.form} action="http://localhost:1234/login" method='POST'>
					<input className="form-control rounded-5" type="email" name="email" placeholder="email" aria-label="default input example"></input>
					<input className="form-control rounded-5" type="password" name="password" placeholder="password" aria-label="default input example"></input>
					<div className="d-grid gap-2">
						<button className="btn btn-primary rounded-5" type="submit">Ingresar</button>
					</div>
				</form>
				<div>
					<p>¿No tenés cuenta?</p>
					<Link to="/register">Registrarse</Link>
				</div>

			</div>


		</div>
	);
}

export default LogIn;
