import { useState } from "react";
import { BrowserRouter as Link } from "react-router-dom";
import styles from './Search.module.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import searchService from './searchServices.js'

function SearchPlaces({ places }) {
  return (
    <div>
      {places.map(place =>
        <Link to={`/${place}`} />)
      }
    </div>
  );
}


  export function Search({ user, handlePlace }) {

    const [click, setClick] = useState(true);
    const [input, setInput] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [places, setPlaces] = useState([])

    const handleInputChange = (e) => {
      e.preventDefault();
      setInput(e.target.value);
      setTimeout(async function () {
        try {
          const place = await searchService.search({ input })

          if (place.state) {
            handlePlace(place);
          } else {
            handlePlace(null);
          }
          setInput('');
        } catch (e) {
          setErrorMessage("Error en el submit");
          console.log(errorMessage);
        }
      }, 300)
    }

    if (click) {
      return (
        <div className={styles.search}>
          <button onClick={() => setClick(false)} type="button" className="btn btn-outline-primary rounded-5">
            <FontAwesomeIcon icon="fa-solid fa-magnifying-glass" />
          </button>
        </div>
      );
    }
    return (
      <div className={styles.search}>
        <form>
          <input value={input} onChange={handleInputChange} className="form-control rounded-5 border-primary text-center" type="text" placeholder="Place" aria-label="default input example"></input>
        </form>
      </div>
    );
  }
