import styles from "./ProfileHeader.module.css"
import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { Popup } from "../../Popup/Popup";

function ProfileHeaderPopUp({user, closeClick}) {

    const handleOverlayClick = (e) => {
        // Cierra el popup solo si el clic ocurre en el fondo (overlay)
        if (Array.from(e.target.classList).some((clase) => /overlay/.test(clase))) {
            closeClick();
            e.stopPropagation();
        }
    }

    const handleProfileClick = (e) => {
        // Cierra el popup solo si el clic ocurre en el fondo (overlay)
        closeClick();
        e.stopPropagation();
    }

    return (
        <Popup close={closeClick} top={true}>

        {/* <div className={styles.overlay} onClick={handleOverlayClick}>
            <div className={styles.popup}> */}
                <div className={styles.popup_options}>
                    <div className={styles.popup_option}>
                        <p>Saldo</p>
                        <p>{user.saldo}</p>
                    </div>
                    <div className={styles.popup_option}>
                        <p>Mis qrckets</p>
                        <p>6</p>
                    </div>
                    <div className={styles.popup_option}>
                        <p>Suscripciones</p>
                        <p>7</p>
                    </div>
                    <div className={styles.popup_option}>
                        <p>Mas opciones</p>
                        <p></p>
                    </div>
                </div>
                <div onClick={handleProfileClick} className={styles.popup_profile}>
                    <p>{user.nombre} <FontAwesomeIcon icon="fa-solid fa-chevron-up" /></p>
                </div>
            {/* </div>
        </div> */}
        </Popup>
    );
}

function ProfileHeader({ user }) {
    const [state, setState] = useState({ click: false })

    const click = () => {setState({click:true})}
    const closeClick = () => {setState({click:false})}

    return (
        <div onClick={click} className={styles.place_profile_header}>
            <p>{user.nombre} <FontAwesomeIcon icon="fa-solid fa-chevron-down" /></p>
            {state.click ? <ProfileHeaderPopUp user={user} closeClick={closeClick}/> : null}
        </div>
    );
}

export default ProfileHeader;
