import React, { useState } from 'react';
import styles from './LogIn.module.css'
import { Link } from 'react-router-dom'
import loginService from './loginServices'
import { Popup } from '../Popup/Popup.js'

function LogIn({close, handleLogIn}) {

	const [errorMessage, setErrorMessage] = useState('');
	const [email, setEmail] = useState('');
	const [password, setPassword] = useState('');

	/* const handleOverlayClick = (e) => {
		// Cierra el popup solo si el clic ocurre en el fondo (overlay)
		if (Array.from(e.target.classList).some((clase) => /overlay/.test(clase))) {
			close();
		}
	} */

	const handleSubmit = async (e) => {
		e.preventDefault();
		try {
			const user = await loginService.login({
				email,
				password
			})
			handleLogIn(user);
			setEmail('');
			setPassword('');
		} catch(e) {
			setErrorMessage("Error en el submit");
			console.log(errorMessage);
		}

	}

	{/* <div className={styles.overlay} onClick={handleOverlayClick}>
		<div className={styles.popup}> */}

	return (
			<Popup up={false}>
				<p>Iniciar Sesión</p>
				<form className={styles.form} onSubmit={handleSubmit}>

					<input className="form-control rounded-5"  
						   type="email"
						   name="email"
						   value={email}
						   placeholder="email"
						   onChange={({target}) => setEmail(target.value)}
						   />

					<input className="form-control rounded-5"
						   type="password"
						   name="password"
						   value={password}		
						   onChange={({target}) => setPassword(target.value)}				   
						   placeholder="password"
						   />
					<div className="d-grid gap-2">
						<button className="btn btn-primary rounded-5" type="submit">Ingresar</button>
					</div>
				</form>
				<div>
					<p>¿No tenés cuenta?</p>
					<Link to="/register">Registrarse</Link>
				</div>
				</Popup>

			/* </div>


		</div> */
	);
}

export default LogIn;
