import React from "react";
import styles from './UserInfo.module.css'
import { Popup } from "../Popup/Popup";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

export function UserInfo({ close, user  }) {

    const handleProfileClick = (e) => {
        // Cierra el popup solo si el clic ocurre en el fondo (overlay)
        close();
        e.stopPropagation();
    }

    return (
        <Popup close={close} top={true}>
            {user ?
                <div className={styles.popup_options}>
                    <div className={styles.popup_option}>
                        <p>Saldo</p>
                        <p>{user.balance}</p>
                    </div>
                    <div className={styles.popup_option}>
                        <p>Mis qrckets</p>
                        <p>6</p>
                    </div>
                    <div className={styles.popup_option}>
                        <p>Suscripciones</p>
                        <p>7</p>
                    </div>
                    <div className={styles.popup_option}>
                        <p>Mas opciones</p>
                        <p></p>
                    </div>
                </div>
                :
                <div className={styles.popup_options}>
                    <div className={styles.popup_option}>
                        <p>Ingresar</p>
                    </div>
                </div>}

            <div onClick={handleProfileClick} className={styles.popup_profile}>
                <p>{user ? "visitante" : "Visitante"} <FontAwesomeIcon icon="fa-solid fa-chevron-up" /></p>
            </div>
        </Popup>
    );
}
