import './App.css';
import React from 'react';
import { library } from '@fortawesome/fontawesome-svg-core'
import { fas } from '@fortawesome/free-solid-svg-icons'
import { faTwitter, faFontAwesome } from '@fortawesome/free-brands-svg-icons'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

library.add(fas, faTwitter, faFontAwesome)

class LogIn extends React.Component {
  constructor(props) {
    super(props);
    this.state = { btn: false };
    this.clickLogIn = this.clickLogIn.bind(this); 
  }

  clickLogIn() {
    this.setState({btn: true});
  }

  render() {
    {
      if (this.state.btn && !this.props.other) {
        return (
          <div id='login' className='border border-primary-1 rounded-5 m-5 p-5'>
            <p>Log In</p>
            <form>
              <div class="form-floating mb-3">
                <input type="text" className="form-control" id="floatingInput" placeholder="Username" />
                <label for="floatingInput">Username</label>
              </div>
              <div class="form-floating">
                <input type="password" className="form-control" id="floatingPassword" placeholder="Password" />
                <label for="floatingPassword">Password</label>
              </div>
            </form>
          </div>
        );
      } else {
        return (
          <div>
            <button type="button" onClick={this.clickLogIn} className="btn btn btn-outline-primary"><FontAwesomeIcon icon="fas fa-user" style={{ color: "#613583", }} /></button>
            <p>Iniciar Sesi√≥n</p>
          </div>
        );
      }
    }

  }
}

class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      login: true,
      register: true,
      place: true
    }
  }

  newClick(btn) {
    switch(btn) {
      case 1: return {login: true,
                      register: false,
                      place: false};
      case 2: return {login: false,
                      register: true,
                      place: false};
      default: return {login: false,
                       register: false,
                       place: true};
    }
  }

  render() {

    return (
      <div id="homepage" className='conteiner-fluid d-flex flex-column  align-items-center justify-content-center'>
        <LogIn other={this.state.login} click={this.newClick}/>
        <div>
          <button type="button" className="btn btn btn-outline-primary"><FontAwesomeIcon icon="fa-solid fa-address-card" style={{ color: "#613583", }} /></button>
          <p>Registrarse</p>
        </div>
        <div>
          <button type="button" className="btn btn btn-outline-primary"><FontAwesomeIcon icon="fas fa-magnifying-glass" style={{ color: "#613583", }} /></button>
          <p>Sucursales</p>
        </div>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <HomePage />
    </div>
  );
}

export default App;
