import { useState } from "react";
import { BrowserRouter as Link } from "react-router-dom";
import styles from './Search.module.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

const url = "http://localhost:3000"

function SearchPlaces({ places }) {
  return (
    <div>
      {places.map(place =>
        <Link to={`/place/:${place}`} />)
      }
    </div>
  );
}


export function Search({ user, handlePlace }) {

  const [click, setClick] = useState(true);
  const [input, setInput] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [places, setPlaces] = useState(null)

  const handleInputChange = (e) => {
    e.preventDefault();
    setInput(e.target.value);
    setTimeout(function () {
      try {
        console.log(url + "/plsaces/" + input);

        fetch(url + "/placesss/" + input)
          .then(response => {
            if (!response.ok) {
              throw new Error("Error en la solicitud HTTP, código " + response.status);
            }
            console.log(response)  
            return response.json();
          })
          .then(data => {
            setPlaces(data)
          })
          .catch(error => {
            setErrorMessage("Error en el submit: " + error);
            console.error(errorMessage);
          });
      } catch (error) {
        console.error("Error en setTimeout:", error);
      }
    }, 1000);
  }

  if (click) {
    return (
      <div className={styles.search}>
        <button onClick={() => setClick(false)} type="button" className="btn btn-outline-primary rounded-5">
          <FontAwesomeIcon icon="fa-solid fa-magnifying-glass" />
        </button>
      </div>
    );
  }
  return (
    <div className={styles.search}>
      <form>
        <input value={input}  className="form-control rounded-5 border-primary text-center" type="text" placeholder="Place" aria-label="default input example"></input>
        <button onClick={() => handleInputChange()}></button>
      </form>
      {places ? <SearchPlaces places={places} /> : null}
    </div>
  );
}
